import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './componentes/Navbar';
import NavbarAdmin from './componentes/NavbarAdmin'; // El Navbar para admins
import Home from './componentes/home';
import AddProduct from './componentes/AddProduct';
import EditProduct from './componentes/EditProduct';
import Login from './componentes/login';
import Register from './componentes/Register';
import AdminDashboard from './componentes/AdminDashboard';
import ManageUsers from './componentes/ManageUsers';
import AdminLogin from './componentes/AdminLogin';

function App() {
  const [user, setUser] = useState(null); // Estado para el usuario autenticado

  // Función para verificar si el usuario es admin
  const isAdmin = user?.role === 'admin';

  return (
    <Router>
      {/* Muestra el Navbar principal siempre */}
      <Navbar user={user} setUser={setUser} />

      {/* Muestra el Navbar de administrador si el usuario es admin */}
      {isAdmin && <NavbarAdmin />}

      <div className="container mt-4">
        <Routes>
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/" element={<Home />} />
          <Route path="/admin-login" element={<AdminLogin />} />
          <Route path="/manage-user" element={<ManageUsers />} />
          <Route path="/navbar-admin" element={<NavbarAdmin />} />

          {/* Rutas protegidas para usuarios administradores */}
          <Route
            path="/add-product"
            element={isAdmin ? <AddProduct /> : <Navigate to="/" />}
          />
          <Route
            path="/edit-product"
            element={isAdmin ? <EditProduct /> : <Navigate to="/" />}
          />
          <Route
            path="/admin-dashboard"
            element={isAdmin ? <AdminDashboard /> : <Navigate to="/" />}
          />
          <Route
            path="/manage-users"
            element={isAdmin ? <ManageUsers /> : <Navigate to="/admin-dashboard" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
