import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [currentUser, setCurrentUser] = useState({ name: '', email: '', role: '', password: '' });

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/users'); // Ajusta si necesitas separar usuarios y admins
        setUsers(response.data);
      } catch (error) {
        console.error('Error al obtener usuarios:', error.response?.data || error.message);
      }
    };
    fetchUsers();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Determinar endpoint según el rol
    const endpoint =
      currentUser.role === 'admin'
        ? 'http://localhost:3000/api//register'
        : 'http://localhost:3000/api/users/register';

    try {
      const response = isEditing
        ? await axios.put(`http://localhost:3000/api/${currentUser.role}s/${currentUser._id}`, currentUser) // Asume que las rutas incluyen 'admins' o 'users'
        : await axios.post(endpoint, currentUser);

      setMessage(isEditing ? 'Usuario actualizado con éxito.' : 'Usuario agregado con éxito.');
      setUsers(isEditing
        ? users.map((user) => (user._id === currentUser._id ? response.data : user))
        : [...users, response.data]);

      setIsEditing(false);
      setCurrentUser({ name: '', email: '', role: '', password: '' });
    } catch (error) {
      console.error('Error al procesar usuario:', error.response?.data || error.message);
      setMessage('Error al procesar el usuario.');
    }
  };

  const handleEdit = (user) => {
    setCurrentUser(user);
    setIsEditing(true);
  };

  return (
    <div>
      <h2>Gestionar Usuarios</h2>
      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nombre"
          value={currentUser.name}
          onChange={(e) => setCurrentUser({ ...currentUser, name: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Correo"
          value={currentUser.email}
          onChange={(e) => setCurrentUser({ ...currentUser, email: e.target.value })}
          required
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={currentUser.password}
          onChange={(e) => setCurrentUser({ ...currentUser, password: e.target.value })}
          required={!isEditing}
        />
        <select
          value={currentUser.role}
          onChange={(e) => setCurrentUser({ ...currentUser, role: e.target.value })}
          required
        >
          <option value="">Seleccionar rol</option>
          <option value="user">Usuario</option>
          <option value="admin">Administrador</option>
        </select>
        <button type="submit">{isEditing ? 'Actualizar Usuario' : 'Agregar Usuario'}</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Email</th>
            <th>Rol</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <button onClick={() => handleEdit(user)}>Editar</button>
                {/* Añade aquí lógica para eliminar si es necesario */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageUsers;
