import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [user, setUser] = useState({
    name: '',
    email: '',
    password: '',
    isAdmin: false,  // Campo para el rol de usuario/admin
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleCheckboxChange = () => {
    setUser({ ...user, isAdmin: !user.isAdmin });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validación de contraseñas
    if (!user.password) {
      return alert('La contraseña es obligatoria');
    }

    try {
      // Realizar la solicitud POST al backend
      const response = await axios.post('http://localhost:5000/api/users/register', user);
      
      // Si el registro es exitoso, guarda el token en el localStorage
      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        // Redirigir al Home después del registro
        navigate('/');
      }
    } catch (error) {
      console.error("Error registrando usuario:", error);
      alert('Error en el registro. Intenta nuevamente.');
    }
  };

  return (
    <div>
      <h2>Registro</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nombre</label>
          <input type="text" className="form-control" name="name" value={user.name} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Correo electrónico</label>
          <input type="email" className="form-control" name="email" value={user.email} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Contraseña</label>
          <input type="password" className="form-control" name="password" value={user.password} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>
            <input type="checkbox" checked={user.isAdmin} onChange={handleCheckboxChange} />
            {' '} Soy Administrador
          </label>
        </div>
        <button type="submit" className="btn btn-primary">Registrar</button>
      </form>
    </div>
  );
}

export default Register;
