import React from 'react';

const Hero = ({ title, subtitle, backgroundImage }) => {
  return (
    <div
      className="hero h-screen flex items-center justify-center bg-cover bg-center text-white"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="text-center bg-black bg-opacity-50 p-6 rounded-lg">
        <h1 className="text-4xl md:text-6xl font-bold mb-4">{title}</h1>
        <p className="text-xl md:text-2xl">{subtitle}</p>
        <button className="mt-6 px-6 py-2 bg-pink-500 hover:bg-pink-600 text-white font-semibold rounded-lg transition">
          Explorar Productos
        </button>
      </div>
    </div>
  );
};

export default Hero;
