import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';  // Importa el archivo CSS

function Navbar() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null); // Estado para almacenar el rol
  const token = localStorage.getItem('token');  // Obtiene el token del localStorage

  useEffect(() => {
    if (token) {
      try {
        // Decodificar el token
        const decodedToken = JSON.parse(atob(token.split('.')[1]));  // Decodificar el JWT
        if (decodedToken && decodedToken.user) {
          setRole(decodedToken.user.role);  // Obtener el rol del token y guardarlo
        } else {
          setRole(null);  // Si el token no tiene el campo user, establecer rol como null
        }
      } catch (error) {
        console.error("Error al decodificar el token:", error);
        setRole(null);  // En caso de error, establecer rol como null
      }
    }
  }, [token]); // Se ejecuta cuando el token cambia

  const handleLogout = () => {
    // Eliminar el token y el rol del localStorage y redirigir al login
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/login');
  };

  return (
    <nav>
      <ul>
        <li><Link to="/">Home</Link></li>

        {/* Si el rol es admin, mostrar opciones de administración */}
        {role === 'admin' && (
          <>
            <li><Link to="/add-product">Agregar Producto</Link></li>
            <li><Link to="/edit-product">Editar Producto</Link></li>
          </>
        )}

        {/* Si no hay token (usuario no autenticado), mostrar Login y Registro */}
        {!token && (
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/register">Registrar</Link></li>
          </>
        )}

        {/* Si el usuario está autenticado, mostrar Logout */}
        {token && <li><button onClick={handleLogout}>Logout</button></li>}
      </ul>
    </nav>
  );
}

export default Navbar;
