import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Cambiado a useNavigate

function EditProduct({ match }) {
  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    quantity: ''
  });

  const navigate = useNavigate(); // Cambiado a useNavigate

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const { data } = await axios.get(`http://localhost:5000/api/products/${match.params.id}`);
        setProduct(data);
      } catch (error) {
        console.error("Error fetching product:", error);
      }
    };
    fetchProduct();
  }, [match.params.id]);

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5000/api/products/${match.params.id}`, product, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    })
    .then(() => {
      navigate('/'); // Cambiado a navigate
    })
    .catch(error => console.error("Error editing product:", error));
  };

  return (
    <div>
      <h2>Editar Producto</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nombre</label>
          <input type="text" className="form-control" name="name" value={product.name} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Descripción</label>
          <input type="text" className="form-control" name="description" value={product.description} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Precio</label>
          <input type="number" className="form-control" name="price" value={product.price} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Categoría</label>
          <input type="text" className="form-control" name="category" value={product.category} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Cantidad</label>
          <input type="number" className="form-control" name="quantity" value={product.quantity} onChange={handleChange} />
        </div>
        <button type="submit" className="btn btn-primary">Guardar Cambios</button>
      </form>
    </div>
  );
}

export default EditProduct;
