import React, { useEffect, useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import '../styles/AdminDashboard.css';
import jwtDecode from 'jwt-decode';

const AdminDashboard = () => {
  const [user, setUser] = useState(null); // Estado para manejar el usuario
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');  // Para manejar errores

  useEffect(() => {
    // Recuperamos el token y decodificamos la información del usuario
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);  // Usamos jwtDecode para decodificar el token
        setUser(decodedToken.user);
      } catch (error) {
        console.error('Error al decodificar el token:', error);
        setError('El token es inválido o ha caducado.');
      }
    } else {
      setError('No hay token de autenticación disponible.');
    }
    setIsLoading(false);
  }, []);

  if (isLoading) {
    return <div>Cargando...</div>;  // Puedes reemplazar esto con un spinner o animación si lo prefieres
  }

  if (error) {
    return <div className="error-message">{error}</div>;  // Mostrar el mensaje de error si hay problemas con el token
  }

  if (!user || user.role !== 'admin') {
    return <Navigate to="/" />;  // Redirigir si no es admin o no hay usuario
  }

  return (
    <div className="admin-dashboard">
      <h1 className="dashboard-title">Panel de Administración</h1>
      <div className="dashboard-options">
        <Link to="/add-product" className="dashboard-link">Agregar Producto</Link>
        <Link to="/edit-product" className="dashboard-link">Editar Producto</Link>
        <Link to="/manage-users" className="dashboard-link">Gestionar Usuarios</Link>
      </div>
    </div>
  );
};

export default AdminDashboard;
