import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/Home.css';

function Home() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);  // Estado para controlar la carga de datos
  const [error, setError] = useState('');  // Estado para manejar errores

  useEffect(() => {
    // Obtener los productos desde la API
    axios.get('http://localhost:3000/api/products')
      .then(response => {
        setProducts(response.data);
        setLoading(false);  // Cuando los productos son cargados, cambiar a no cargando
      })
      .catch(error => {
        console.error("Error fetching products:", error);
        setError("No se pudieron cargar los productos. Intenta de nuevo más tarde.");
        setLoading(false);  // En caso de error también dejar de mostrar la carga
      });
  }, []);

  // Mostrar loading mientras se obtienen los datos
  if (loading) {
    return <div className="loading-message">Cargando productos...</div>;
  }

  // Mostrar error si hubo un problema al obtener los productos
  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="home-container">
      <h2 className="store-title">MAKEUPJM</h2>
      <p className="store-description">¡Explora nuestra colección de productos de maquillaje de alta calidad!</p>
      
      {products.length === 0 ? (
        <div className="no-products-message">No hay productos disponibles en este momento.</div>
      ) : (
        <div className="product-grid">
          {products.map(product => (
            <div className="product-card" key={product._id}>
              <img
                src={product.imageUrl || 'https://via.placeholder.com/200'}  // Imagen de producto con fallback
                alt={product.name}
                className="product-image"
              />
              <div className="product-details">
                <h5 className="product-name">{product.name}</h5>
                <p className="product-description">{product.description}</p>
                <div className="product-info">
                  <p className="product-price"><strong>Precio:</strong> ${product.price}</p>
                  <p className="product-quantity"><strong>Cantidad:</strong> {product.quantity}</p>
                </div>
                <button className="btn-add-to-cart">Añadir al carrito</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Home;
