import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false); // Para manejar el estado de carga
  const [error, setError] = useState(''); // Para mostrar un error si ocurre

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    setLoading(true); // Mostrar que está cargando
    setError(''); // Limpiar cualquier error anterior

    try {
      const response = await axios.post('http://localhost:3000/api/users/login', { email, password });
      const { token } = response.data;

      // Guardar el token en localStorage
      localStorage.setItem('token', token);

      // Redirigir según el rol
      const decodedToken = JSON.parse(atob(token.split('.')[1]));
      if (decodedToken.role === 'admin') {
        navigate('/admin-dashboard'); // Redirigir a una página de admin
      } else {
        navigate('/'); // Redirigir a una página de usuario
      }
    } catch (error) {
      console.error("Error en el login:", error);
      setError('Credenciales incorrectas.'); // Establecer un error si la autenticación falla
    } finally {
      setLoading(false); // Dejar de mostrar el estado de carga
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      {error && <div className="alert alert-danger">{error}</div>} {/* Mostrar mensaje de error si hay */}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Correo electrónico</label>
          <input
            type="email"
            id="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Contraseña</label>
          <input
            type="password"
            id="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Cargando...' : 'Iniciar sesión'}
        </button>
      </form>
    </div>
  );
}

export default Login;
