import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EditProduct = ({ productId, onProductUpdated }) => {
  const [product, setProduct] = useState(null);
  const [message, setMessage] = useState('');

  // Obtener datos del producto al cargar el componente
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/products/`);
        setProduct(response.data);
      } catch (error) {
        setMessage('Error al obtener el producto');
        console.error(error);
      }
    };

    fetchProduct();
  }, [productId]);

  // Manejar cambios en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  // Actualizar el producto
  const handleUpdate = async () => {
    try {
      const response = await axios.put(`http://localhost:3000/api/products/${productId}`, product);
      setMessage('Producto actualizado exitosamente');
      onProductUpdated(response.data.product);
    } catch (error) {
      setMessage('Error al actualizar el producto');
      console.error(error);
    }
  };

  // Eliminar el producto
  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:3000/api/products/${productId}`);
      setMessage('Producto eliminado exitosamente');
      onProductUpdated(null); // Indicar que el producto ha sido eliminado
    } catch (error) {
      setMessage('Error al eliminar el producto');
      console.error(error);
    }
  };

  if (!product) {
    return <p>Cargando datos del producto...</p>;
  }

  return (
    <div className="edit-product-form">
      <h2>Editar Producto</h2>
      {message && <p>{message}</p>}

      <form>
        <div>
          <label>Nombre del Producto:</label>
          <input
            type="text"
            name="name"
            value={product.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Descripción:</label>
          <input
            type="text"
            name="description"
            value={product.description}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Precio:</label>
          <input
            type="number"
            name="price"
            value={product.price}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Categoría:</label>
          <input
            type="text"
            name="category"
            value={product.category}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Cantidad:</label>
          <input
            type="number"
            name="quantity"
            value={product.quantity}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>URL de la Imagen:</label>
          <input
            type="text"
            name="image"
            value={product.image}
            onChange={handleChange}
          />
        </div>
      </form>

      <div className="buttons mt-4">
        <button
          className="px-4 py-2 bg-blue-500 text-white rounded mr-2"
          onClick={handleUpdate}
        >
          Actualizar Producto
        </button>
        <button
          className="px-4 py-2 bg-red-500 text-white rounded"
          onClick={handleDelete}
        >
          Eliminar Producto
        </button>
      </div>
    </div>
  );
};

export default EditProduct;
