import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    // Obtener el token del localStorage
    const token = localStorage.getItem('token');
    
    if (token) {
      // Decodificar el token JWT para obtener el rol (si usas JWT)
      const decodedToken = jwt.decode(token);
      if (decodedToken && decodedToken.isAdmin) {
        setIsAdmin(true);
      }
    }
  }, []);

  return (
    <nav>
      <ul>
        {/* Enlaces públicos */}
        <li><Link to="/">Inicio</Link></li>
        <li><Link to="/login">Login</Link></li>

        {/* Opciones solo para administradores */}
        {isAdmin && (
          <>
            <li><Link to="/admin-dashboard">Panel de Administrador</Link></li>
            <li><Link to="/add-product">Agregar Producto</Link></li>
            {/* Aquí puedes agregar más opciones administrativas */}
          </>
        )}

        {/* Opciones generales */}
        <li><Link to="/profile">Mi Perfil</Link></li>
      </ul>
    </nav>
  );
}

export default Navbar;
