import React, { useEffect, useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import '../styles/AdminDashboard.css';


const AdminDashboard = () => {
  const [user, setUser] = useState(null);  // Estado para manejar el usuario
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulando la recuperación del usuario desde el almacenamiento local o una API
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);  // Usando jwtDecode para decodificar el token
        setUser(decodedToken.user);
      } catch (error) {
        console.error('Error al decodificar el token:', error);
      }
    }
    setIsLoading(false);
  }, []);

  if (isLoading) {
    return <div>Cargando...</div>;  // Mostrar un cargando mientras se obtiene el usuario
  }

  if (!user || user.role !== 'admin') {
    return <Navigate to="/" />;  // Redirigir a la página de inicio si no es admin
  }

  return (
    <div className="admin-dashboard">
      <h1 className="dashboard-title">Panel de Administración</h1>
      <div className="dashboard-options">
        <Link to="/add-product" className="dashboard-link">Agregar Producto</Link>
        <Link to="/edit-product" className="dashboard-link">Editar Producto</Link>
        <Link to="/manage-users" className="dashboard-link">Gestionar Usuarios</Link>
      </div>
    </div>
  );
};

export default AdminDashboard;
