import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './componentes/Navbar';
import Home from './componentes/home';
import AddProduct from './componentes/AddProduct';
import EditProduct from './componentes/EditProduct';
import Login from './componentes/login';
import Register from './componentes/Register';
import AdminDashboard from './componentes/AdminDashboard';
import ManageUsers from './componentes/ManageUsers';
import ProtectedRoute from './componentes/ProtectedRoute';
function App() {
  const [user, setUser] = useState(null); // Estado del usuario (autenticado)

  // Simula un usuario logueado (cambia esto según tu implementación real)
  const isAuthenticated = Boolean(user); 
  const isAdmin = user?.role === 'admin'; // Comprueba si el usuario es admin

  return (
    <Router>
      <Navbar />
      <div className="container mt-4">
        <Routes>
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/" element={<Home />} />

          {/* Rutas protegidas */}
          <Route
            path="/add-product"
            element={<ProtectedRoute element={AddProduct} isAuthenticated={isAuthenticated} isAdmin={isAdmin} requiredAdmin />}
          />
          <Route
            path="/edit-product"
            element={<ProtectedRoute element={EditProduct} isAuthenticated={isAuthenticated} isAdmin={isAdmin} requiredAdmin />}
          />
          <Route
            path="/admin-dashboard"
            element={<ProtectedRoute element={AdminDashboard} isAuthenticated={isAuthenticated} isAdmin={isAdmin} requiredAdmin />}
          />
          <Route
            path="/manage-users"
            element={<ProtectedRoute element={ManageUsers} isAuthenticated={isAuthenticated} isAdmin={isAdmin} requiredAdmin />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
