import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';

function EditProduct() {
  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    quantity: ''
  });

  const { id } = useParams();
  const history = useHistory();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/products/${id}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    })
    .then(response => setProduct(response.data))
    .catch(error => console.error("Error fetching product:", error));
  }, [id]);

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5000/api/products/${id}`, product, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    })
    .then(() => {
      history.push('/');
    })
    .catch(error => console.error("Error updating product:", error));
  };

  return (
    <div>
      <h2>Editar Producto</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nombre</label>
          <input type="text" className="form-control" name="name" value={product.name} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Descripción</label>
          <input type="text" className="form-control" name="description" value={product.description} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Precio</label>
          <input type="number" className="form-control" name="price" value={product.price} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Categoría</label>
          <input type="text" className="form-control" name="category" value={product.category} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Cantidad</label>
          <input type="number" className="form-control" name="quantity" value={product.quantity} onChange={handleChange} />
        </div>
        <button type="submit" className="btn btn-primary">Actualizar Producto</button>
      </form>
    </div>
  );
}

export default EditProduct;
