import React from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import '../styles/Na';  // Importa el archivo CSSAuthentication

function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/login');
  };

  return (
    <nav>
      <ul>
        <li><Link to="/">Home</Link></li>

        {/* Si el rol es admin, mostrar opciones de administración */}
        {role === 'admin' && (
          <>
            <li><Link to="/add-product">Agregar Producto</Link></li>
            <li><Link to="/edit-product">Editar Producto</Link></li>
          </>
        )}

        {/* Si no hay token (usuario no autenticado), mostrar Login y Registro */}
        {!token && (
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/register">Registrar</Link></li>
          </>
        )}

        {/* Si el usuario está autenticado, mostrar Logout */}
        {token && <li><button onClick={handleLogout}>Logout</button></li>}
      </ul>
    </nav>
  );
}

export default Navbar;
