import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Cambiado a useNavigate

function Login() {
  const [user, setUser] = useState({
    email: '',
    password: ''
  });

  const navigate = useNavigate(); // Cambiado a useNavigate

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/users/login', user)
      .then((response) => {
        // Suponiendo que la respuesta incluye un token
        localStorage.setItem('token', response.data.token);
        navigate('/'); // Cambiado a navigate
      })
      .catch(error => console.error("Error logging in:", error));
  };

  return (
    <div>
      <h2>Iniciar sesi칩n</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Correo electr칩nico</label>
          <input type="email" className="form-control" name="email" value={user.email} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Contrase침a</label>
          <input type="password" className="form-control" name="password" value={user.password} onChange={handleChange} />
        </div>
        <button type="submit" className="btn btn-primary">Iniciar sesi칩n</button>
      </form>
    </div>
  );
}

export default Login;
