import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import ''

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false); // Estado para el rol de administrador
  const [message, setMessage] = useState('');
  const navigate = useNavigate();  // Hook de React Router

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Determinamos la ruta dependiendo si es admin o no
      const url = isAdmin
        ? 'http://localhost:3000/api/users/register/admin' // Ruta para admins
        : 'http://localhost:3000/api/users/register/user';  // Ruta para usuarios normales

      const response = await axios.post(url, {
        name,
        email,
        password,
      });

      setMessage(response.data.message);

      // Redirigir según el rol
      if (isAdmin) {
        navigate('/admin-dashboard'); // Redirige al dashboard de admin
      } else {
        navigate('/home'); // Redirige a una página de usuario normal
      }
    } catch (error) {
      console.error('Error registrando usuario:', error.response?.data || error.message);
      setMessage('Error al registrar el usuario.');
    }
  };

  return (
    <div>
      <h2>Registro de Usuario</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Correo Electrónico:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Contraseña:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>
            <input
              type="checkbox"
              checked={isAdmin}
              onChange={(e) => setIsAdmin(e.target.checked)} // Actualiza el estado del checkbox
            />
            Registrar como Administrador
          </label>
        </div>
        <button type="submit">Registrar</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Register;
