import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/ManageUsers.css'; // Archivo CSS para estilos

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [currentUser, setCurrentUser] = useState({ name: '', email: '', role: '' });
  
  useEffect(() => {
    // Obtener los usuarios al cargar el componente
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/users');
        setUsers(response.data);
      } catch (error) {
        console.error('Error al obtener los usuarios:', error);
      }
    };
    fetchUsers();
  }, []);
  
  // Función para agregar o editar usuario
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isEditing) {
      try {
        await axios.put(`http://localhost:3000/api/users/${currentUser._id}`, currentUser);
        setMessage('Usuario actualizado con éxito.');
        setUsers(users.map((user) => (user._id === currentUser._id ? currentUser : user)));
        setIsEditing(false);
      } catch (error) {
        console.error('Error al actualizar el usuario:', error);
        setMessage('Error al actualizar el usuario.');
      }
    } else {
      try {
        const response = await axios.post('http://localhost:3000/api/users', currentUser);
        setMessage('Usuario agregado con éxito.');
        setUsers([...users, response.data]);
      } catch (error) {
        console.error('Error al agregar usuario:', error);
        setMessage('Error al agregar el usuario.');
      }
    }
    setCurrentUser({ name: '', email: '', role: '' }); // Limpiar el formulario
  };

  // Función para eliminar usuario
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/api/users/${id}`);
      setMessage('Usuario eliminado con éxito.');
      setUsers(users.filter((user) => user._id !== id)); // Actualizar la lista
    } catch (error) {
      console.error('Error al eliminar usuario:', error);
      setMessage('Error al eliminar el usuario.');
    }
  };

  // Función para editar usuario
  const handleEdit = (user) => {
    setCurrentUser(user);
    setIsEditing(true);
  };

  return (
    <div className="manage-users">
      <h2>Gestionar Usuarios</h2>
      {message && <p>{message}</p>}
      
      {/* Formulario para agregar/editar usuario */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nombre"
          value={currentUser.name}
          onChange={(e) => setCurrentUser({ ...currentUser, name: e.target.value })}
        />
        <input
          type="email"
          placeholder="Correo electrónico"
          value={currentUser.email}
          onChange={(e) => setCurrentUser({ ...currentUser, email: e.target.value })}
        />
        <select
          value={currentUser.role}
          onChange={(e) => setCurrentUser({ ...currentUser, role: e.target.value })}
        >
          <option value="">Seleccionar rol</option>
          <option value="user">Usuario</option>
          <option value="admin">Administrador</option>
        </select>
        <button type="submit">{isEditing ? 'Actualizar Usuario' : 'Agregar Usuario'}</button>
      </form>

      {/* Tabla de usuarios */}
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Email</th>
            <th>Rol</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <button onClick={() => handleEdit(user)}>Editar</button>
                <button onClick={() => handleDelete(user._id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageUsers;
