import React from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

function Navbar() {
  const navigate = useNavigate();

  // Verificar si el usuario está autenticado y obtener el rol
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');  // Se espera que el rol esté guardado en localStorage

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/login');  // Redirige al login después de hacer logout
  };

  return (
    <nav>
      <ul>
        <li><Link to="/">Home</Link></li>

        {/* Mostrar las opciones de admin solo si el usuario tiene rol de admin */}
        {role === 'admin' && (
          <>
            <li><Link to="/add-product">Agregar Producto</Link></li>
            <li><Link to="/edit-product">Editar Producto</Link></li>
          </>
        )}

        {/* Mostrar Login si el usuario no está autenticado */}
        {!token && <li><Link to="/login">Login</Link></li>}

        {/* Mostrar Logout solo si el usuario está autenticado */}
        {token && <li><button onClick={handleLogout}>Logout</button></li>}
      </ul>
    </nav>
  );
}

export default Navbar;
