import React from 'react';
import { Link } from 'react-router-dom';

function Navbar() {
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');  // Obtener el rol desde el localStorage

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <Link className="navbar-brand" to="/">Tienda</Link>
      <div className="collapse navbar-collapse">
        <ul className="navbar-nav ml-auto">
          {token && role === 'admin' && (
            <>
              <li className="nav-item">
                <Link className="nav-link" to="/add-product">Agregar Producto</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/edit-product">Editar Producto</Link>
              </li>
            </>
          )}
          <li className="nav-item">
            <Link className="nav-link" to="/login">Iniciar Sesi√≥n</Link>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
