import React from "react";

const Hero = () => {
  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center text-center p-6">
      <div className="max-w-4xl bg-white shadow-lg rounded-2xl p-8">
        <h1 className="text-5xl font-bold text-gray-800 mb-4">MAKEUPJM</h1>
        <p className="text-gray-600 text-lg leading-relaxed">
          Welcome to <span className="font-semibold">makeJm</span>, where innovation meets simplicity. Explore a platform
          designed to streamline your projects, foster creativity, and deliver results efficiently. Join us and take the
          first step towards realizing your potential.
        </p>
        <div className="mt-6 flex flex-col sm:flex-row gap-4 justify-center">
          <button className="px-6 py-3 bg-blue-500 text-white rounded-lg font-semibold shadow hover:bg-blue-600 transition">
            Learn More
          </button>
          <button className="px-6 py-3 bg-gray-200 text-gray-800 rounded-lg font-semibold shadow hover:bg-gray-300 transition">
            Get Started
          </button>
        </div>
      </div>
    </div>
  );
};

const InfoSection = () => {
  return (
    <section className="bg-gray-50 py-12 px-6">
      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8">
        <div>
          <h2 className="text-3xl font-bold text-gray-800 mb-4">About MAKEUPJM</h2>
          <p className="text-gray-600 leading-relaxed">
            At makeJm, we believe in creating solutions that are not only effective but also user-friendly. Our platform
            is built with the latest technology to ensure seamless experiences for users across various domains.
          </p>
        </div>
        <div>
          <h2 className="text-3xl font-bold text-gray-800 mb-4">Why Choose Us?</h2>
          <ul className="text-gray-600 list-disc pl-5 leading-relaxed">
            <li>Intuitive and modern design</li>
            <li>Comprehensive features tailored to your needs</li>
            <li>Dedicated support and regular updates</li>
            <li>Trusted by thousands of users worldwide</li>
          </ul>
        </div>
      </div>
    </section>
  );
};

const HomePage = () => {
  return (
    <div>
      <Hero />
      <InfoSection />
    </div>
  );
};

export default HomePage;
