import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

import '../styles/Navbar.css';
import { jwtDecode } from 'jwt-decode';

const Navbar = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Estado para el inicio de sesión
  const [isAdmin, setIsAdmin] = useState(false); // Estado para el rol de administrador

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      setIsLoggedIn(true);
      try {
        // Decodificar el token para obtener el rol del usuario
        const decodedToken = jwtDecode(token);
        console.log(decodedToken);
        setIsAdmin(decodedToken?.role === 'admin');
      } catch (error) {
        console.error('Error al decodificar el token:', error);
        setIsAdmin(false);
      }
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setIsAdmin(false);
    navigate('/login');
  };

  return (
    <nav>
      <ul>
        {/* Enlace de inicio (siempre visible) */}
        <li><Link to="/">Inicio</Link></li>

        {/* Enlace de registro (solo visible cuando no se ha iniciado sesión) */}
        {!isLoggedIn && <li><Link to="/register">Registrar</Link></li>}

        {/* Enlace de login (solo visible cuando no se ha iniciado sesión) */}
        {!isLoggedIn && <li><Link to="/login">Login</Link></li>}

        {/* Opciones administrativas (solo visibles para admin) */}
        {isAdmin && (
          <>
            <li><Link to="/admin-dashboard">Panel de Administrador</Link></li>
            <li><Link to="/add-product">Agregar Producto</Link></li>
            <li><Link to="/manage-users">Gestionar Usuarios</Link></li> {/* Opciones de administración de usuarios */}
          </>
        )}

        {/* Logout (solo visible cuando el usuario está logueado) */}
        {isLoggedIn && (
          <li>
            <button onClick={handleLogout}>Logout</button>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
