import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false); // Variable para determinar si es admin
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const url = isAdmin
        ? 'http://localhost:3000/api/users/login/admin'  // Ruta para login admin
        : 'http://localhost:3000/api/users/login';  // Ruta para login usuario normal

      const response = await axios.post(url, { email, password });

      // Guardar el token en localStorage
      localStorage.setItem('token', response.data.token);

      // Redirigir según el tipo de usuario
      if (isAdmin) {
        navigate('/admin-dashboard');
      } else {
        navigate('/');
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Correo Electrónico:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Contraseña:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>
            <input
              type="checkbox"
              checked={isAdmin}
              onChange={(e) => setIsAdmin(e.target.checked)} // Cambia el estado cuando se marque el checkbox
            />
            Iniciar sesión como Administrador
          </label>
        </div>
        <button type="submit">Iniciar sesión</button>
      </form>

      <p>¿No eres administrador? <Link to="/login/admin">Inicia sesión como administrador</Link></p>

    </div>
  );
};

export default Login;
