import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from './context/AuthContext'; // Asegúrate de importar el contexto correctamente

const Navbar = () => {
  const { user, isLoading, isAdmin, logout } = useAuth();

  if (isLoading) {
    return <div>Cargando...</div>;
  }

  return (
    <nav>
      <ul>
        <li><Link to="/">Inicio</Link></li>
        {!user && <li><Link to="/login">Login</Link></li>}
        {!user && <li><Link to="/register">Registrar</Link></li>}
        {isAdmin && (
          <>
            <li><Link to="/admin-dashboard">Panel de Administrador</Link></li>
            <li><Link to="/add-product">Agregar Producto</Link></li>
          </>
        )}
        {user && (
          <li>
            <button onClick={logout}>Logout</button>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
