import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './componentes/Navbar';
import ProductList from './componentes/ProductList';
import AddProduct from './componentes/AddProduct';
import EditProduct from './componentes/EditProduct';  {/* Asegúrate de que este sea el componente correcto */}
import Login from './componentes/Login';
import Register from './componentes/Register';

function App() {
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role'); // Obtener el rol del usuario desde localStorage

  // Función para proteger las rutas de administración
  const ProtectedRoute = ({ children, requiredRole }) => {
    if (!token) {
      return <Navigate to="/login" />;
    }
    if (role !== requiredRole) {
      return <Navigate to="/" />;
    }
    return children;
  };

  return (
    <Router>
      <Navbar />
      <div className="container mt-4">
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/add-product"
            element={
              <ProtectedRoute requiredRole="admin">
                <AddProduct />
              </ProtectedRoute>
            }
          />
          <Route
            path="/edit-product/:id"
            element={
              <ProtectedRoute requiredRole="admin">
                <EditProduct />
              </ProtectedRoute>
            }
          />
          <Route path="/" element={<ProductList />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
