import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Cambiado a useNavigate

function Register() {
  const [user, setUser] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    isAdmin: false,  // Nuevo campo para el rol de usuario/admin
  });

  const navigate = useNavigate(); // Cambiado a useNavigate

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleCheckboxChange = () => {
    setUser({ ...user, isAdmin: !user.isAdmin });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (user.password !== user.confirmPassword) {
      return alert('Las contraseñas no coinciden');
    }

    axios.post('http://localhost:5000/api/users/register', user)
      .then(() => {
        navigate('/login'); // Cambiado a navigate
      })
      .catch(error => console.error("Error registering user:", error));
  };

  return (
    <div>
      <h2>Registro</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nombre</label>
          <input type="text" className="form-control" name="name" value={user.name} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Correo electrónico</label>
          <input type="email" className="form-control" name="email" value={user.email} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Contraseña</label>
          <input type="password" className="form-control" name="password" value={user.password} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Confirmar contraseña</label>
          <input type="password" className="form-control" name="confirmPassword" value={user.confirmPassword} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>
            <input type="checkbox" checked={user.isAdmin} onChange={handleCheckboxChange} />
            {' '} Soy Administrador
          </label>
        </div>
        <button type="submit" className="btn btn-primary">Registrar</button>
      </form>
    </div>
  );
}

export default Register;
