import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
 // Asegúrate de tener un estilo adecuado para el formulario

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Hacer la solicitud al servidor para login
      const response = await axios.post('http://localhost:3000/api/login', {
        email,
        password,
      });

      // Guardar el token en localStorage y redirigir
      localStorage.setItem('token', response.data.token);
      setMessage('');
      navigate('/');
    } catch (error) {
      setMessage('Error al iniciar sesión. Verifica tus credenciales.');
    }
  };

  const handleAdminLogin = () => {
    // Redirigir al login de administrador (si tu ruta está definida para login admin)
    navigate('/login/admin');
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Correo Electrónico:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Contraseña:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Iniciar sesión</button>
      </form>
      {message && <p>{message}</p>}

      {/* Opción para ingresar como administrador */}
      <div>
        <button onClick={handleAdminLogin}>Ingresar como Administrador</button>
      </div>
    </div>
  );
};

export default Login;
